    fun generatePassword(length: Int, complexity: Int) {
        val random = SecureRandom()
        val lowercase = "abcdefghijklmnopqrstuvwxyz"
        val uppercase = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
        val digits = "0123456789"
        val specialChars = "!@#$%^&*()_-+=[]{}|;:,.<>?/~"

        var password = ""

        val chars = when (complexity) {
            1 -> lowercase + uppercase + digits
            2 -> lowercase + uppercase + digits + specialChars
            else -> lowercase + uppercase + digits + specialChars + specialChars
        }

        for (i in 0 until length) {
            password += chars[random.nextInt(chars.length)]
        }
        PAROLGENERATOR.save(password)
    }

